package basic;

public class Foreachloop {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//foreachloop
		int ar[]= {1,2,3,4,5};
		String a="hello";
		for(int p:ar) {
			System.out.println(p);
			
		}
		//

		String word[]=a.split("e");
		for(String d:word) {
		System.out.println(d);
		}
		
		


	}

}
